const ReviewCard = ({ logo, name }: { logo: string | ReactNode; name: string }) => {
	return (
		<div
			className={
				'relative min-w-[250px] flex justify-center items-center overflow-hidden rounded-xl border bg-slate-800/50 border-slate-900 w-full py-4 px-12 transition hover:bg-slate-800/75 hover:border-slate-900/75 hover:shadow-lg group'
			}
		>
			<div className='flex flex-row grayscale-[100%] items-center justify-center w-full h-auto gap-2 text-white transition group-hover:scale-110 hover:grayscale-0'>
				{logo}
				{name}
			</div>
		</div>
	)
}

export const LF = () => {
	return (
		<section className='flex flex-col flex-wrap items-center justify-center my-48'>
			<h2 className='xl:text-6xl md:text-4xl text-2xl font-bold text-center text-sky-100'>
				Frames y Lenguajes que uso
			</h2>
			<VideoReactIcon />
			<p className='max-w-lg text-xl text-sky-200 text-center [†ext-wrap:balance] mt-4'></p>
			<div className='relative flex flex-col items-center justify-center w-full h-full gap-4 py-20 overflow-hidden rounded-lg bg-background'>
				<Marquee pauseOnHover className='[--duration:40s]'>
					{Lang.map((review) => (
						<ReviewCard key={review.name} {...review} />
					))}
				</Marquee>
				<Marquee reverse pauseOnHover className='[--duration:40s]'>
					{Lang
						.slice()
						.reverse()
						.map((review) => (
							<ReviewCard key={review.name} {...review} />
						))}
				</Marquee>
				<div className='absolute inset-y-0 left-0 w-40 pointer-events-none from-[#000] to-transparent bg-gradient-to-r '></div>
				<div className='absolute inset-y-0 right-0 w-1/3 pointer-events-none bg-gradient-to-l from-[#000]'></div>
			</div>
		</section>
	)
}
